// Disable XML-RPC completely
add_filter('xmlrpc_enabled', '__return_false');

// Remove XML-RPC headers
remove_action('wp_head', 'rsd_link');
remove_action('wp_head', 'wlwmanifest_link');

// Disable file editing in admin
if (!defined('DISALLOW_FILE_EDIT')) {
    define('DISALLOW_FILE_EDIT', true);
}

// Remove WordPress version from head
remove_action('wp_head', 'wp_generator');

// Remove from RSS feeds
add_filter('the_generator', '__return_empty_string');

// Generic login error message
add_filter('login_errors', function() {
    return 'Invalid login credentials. Please try again.';
});

// Disable WordPress emojis
add_action('init', function() {
    remove_action('wp_head', 'print_emoji_detection_script', 7);
    remove_action('admin_print_scripts', 'print_emoji_detection_script');
    remove_action('wp_print_styles', 'print_emoji_styles');
    remove_action('admin_print_styles', 'print_emoji_styles');
});

// Reduce Heartbeat frequency in admin (default is 15 seconds)
add_filter('heartbeat_settings', function($settings) {
    $settings['interval'] = 60; // 60 seconds
    return $settings;
});

// Show free shipping progress notice
add_action('woocommerce_before_cart', function() {
    $min_amount = 50; // Minimum order for free shipping
    $current = WC()->cart->subtotal;

    if ($current < $min_amount) {
        $remaining = $min_amount - $current;
        wc_print_notice(
            sprintf('Add %s more to get FREE shipping!', wc_price($remaining)),
            'notice'
        );
    }
});

// Custom admin footer text
add_filter('admin_footer_text', function() {
    return 'Powered by <strong>Your Company Name</strong>. Built with WordPress.';
});

// Remove unwanted dashboard widgets
add_action('wp_dashboard_setup', function() {
    remove_meta_box('dashboard_quick_press', 'dashboard', 'side');
    remove_meta_box('dashboard_primary', 'dashboard', 'side');
    remove_action('welcome_panel', 'wp_welcome_panel');
}, 20);

// Custom login logo using site icon
add_action('login_enqueue_scripts', function() {
    $icon = get_site_icon_url(84);
    if ($icon) {
        echo '<style>#login h1 a { background-image: url(' . esc_url($icon) . '); background-size: contain; width: 84px; height: 84px; }</style>';
    }
});

add_filter('login_headerurl', function() { return home_url(); });

// Set JPEG quality (1-100, default is 82)
add_filter('jpeg_quality', function() {
    return 85;
});

// Simple maintenance mode
add_action('template_redirect', function() {
    if (!current_user_can('manage_options') && !is_admin()) {
        wp_die(
            '<h1>Site Under Maintenance</h1><p>We are performing scheduled maintenance. Please check back soon!</p>',
            'Maintenance Mode',
            array('response' => 503)
        );
    }
});

// Remove WooCommerce Zoom Effect
add_action('after_setup_theme', function() {
    remove_theme_support('wc-product-gallery-zoom');
}, 100);

// Add Sold column to Products table
add_filter('manage_edit-product_columns', function($columns) {
    $columns['product_sold'] = 'Sold';
    return $columns;
});

add_action('manage_product_posts_custom_column', function($column, $post_id) {
    if ($column === 'product_sold') {
        echo get_post_meta($post_id, 'total_sales', true) ?: '0';
    }
}, 10, 2);

// Add categories to pages
add_action('init', function() {
    register_taxonomy_for_object_type('category', 'page');
});

add_filter('woocommerce_checkout_fields', function($fields) {
    $virtual_only = true;
    foreach (WC()->cart->get_cart() as $item) {
        if (!$item['data']->is_virtual() && !$item['data']->is_downloadable()) {
            $virtual_only = false;
            break;
        }
    }
    if ($virtual_only) {
        unset($fields['billing']['billing_address_1']);
        unset($fields['billing']['billing_address_2']);
        unset($fields['billing']['billing_city']);
        unset($fields['billing']['billing_state']);
        unset($fields['billing']['billing_postcode']);
    }
    return $fields;
});

// Add Registration Date column (unique prefixed function names)
add_filter('manage_users_columns', function($columns) {
    $columns['registration_date'] = 'Registered';
    return $columns;
});

add_filter('manage_users_custom_column', function($output, $column, $user_id) {
    if ($column === 'registration_date') {
        $user = get_userdata($user_id);
        return date('j M, Y H:i', strtotime($user->user_registered));
    }
    return $output;
}, 10, 3);

add_filter('manage_users_sortable_columns', function($columns) {
    $columns['registration_date'] = 'registered';
    return $columns;
});
